mapper.delete:
        delete from vmes_warehouse_in_execute
        where id in

		#{item}

mapper.select:
select b.parent_id, a.detail_id detailId, sum(a.count) executeCount from vmes_warehouse_in_execute a left join vmes_warehouse_in_detail b on a.detail_id = b.id
and b.parent_id = #{parentId}
and a.detail_id = #{detailId}
and a.isdisable = '1'
group by b.parent_id,a.detail_id

select
    id id,
    detail_id detailId,
    executor_id executorId,
    warehouse_id warehouseId,
    count count,

    cuser cuser,
    cdate cdate,
    uuser uuser,
    udate udate,
    remark remark,

    isdisable isdisable
from vmes_warehouse_in_execute



        where 1=2







                         and id <> #{id} 


                        and id = #{id}




                and detail_id = #{detailId}


                and executor_id = #{executorId}




                and ${queryStr}





    order by ${orderStr}

select id id, detail_id detailId, warehouse_id warehouseId, count count, cuser cuser,
cdate cdate, uuser uuser, udate udate, remark remark, isdisable isdisable from vmes_warehouse_in_execute
and ${queryStr}
and code LIKE CONCAT(CONCAT('%', #{code}),'%')
and name LIKE CONCAT(CONCAT('%', #{name}),'%')
order by ${orderStr}

select execute.id, execute.detail_id detailId, execute.executor_id executorId, execute.warehouse_id warehouseId, round(ifnull(execute.count, 0), 2) count, round(ifnull(execute.count, 0), 2) beforeCount, execute.remark,
warehouse.path_name pathName, ifnull(warehouseProduct.stock_count, 0) stockCount from vmes_warehouse_in_execute execute left join vmes_warehouse warehouse on execute.warehouse_id = warehouse.id left join ( select product_id,company_id,warehouse_id,round(sum(ifnull(stock_count,0)), 2) stock_count from vmes_warehouse_product
and product_id = #{productId}
and company_id = #{companyId}
group by product_id,company_id,warehouse_id ) warehouseProduct on execute.warehouse_id = warehouseProduct.warehouse_id
and execute.detail_id = #{detailId}
and execute.executor_id = #{executorId}
and execute.isdisable = '1'
