mapper.delete:
delete from vmes_warehouse_in_execute
where id in

    #{item}

mapper.select:
select
    id id,
    detail_id detailId,
    executor_id executorId,
    cuser cuser,
    cdate cdate,

    uuser uuser,
    udate udate,
    remark remark,
    isdisable isdisable
from vmes_warehouse_in_executor



        where 1=2







                         and id <> #{id} 


                        and id = #{id}




                and detail_id = #{detailId}


                and executor_id = #{executorId}




                and ${queryStr}





    order by ${orderStr}

select executor.id id, executor.detail_id detailId, executor.executor_id executorId, user.user_name executorName, executor.cuser cuser, DATE_FORMAT(executor.cdate,'%Y-%m-%d %T') cdate,
executor.uuser uuser, executor.udate udate, executor.remark remark, executor.isdisable isdisable from vmes_warehouse_in_executor executor left join vmes_user user on executor.executor_id = user.id
and executor.detail_id = #{detailId}
and user.user_name LIKE CONCAT(CONCAT('%', #{executor}),'%')
and ${queryStr}
order by ${orderStr}

select executor.id, executor.detail_id detailId, executor.executor_id executorId,
detail.warehouse_id warehouseId,
warehouse.path_name pathName, ifnull(warehouseProduct.stock_count, 0) stockCount, '' count from vmes_warehouse_in_executor executor left join vmes_warehouse_in_detail detail on executor.detail_id = detail.id left join vmes_warehouse warehouse on detail.warehouse_id = warehouse.id left join ( select product_id,company_id,warehouse_id,round(sum(ifnull(stock_count,0)), 2) stock_count from vmes_warehouse_product
and product_id = #{productId}
and company_id = #{companyId}
and warehouse_id = #{warehouseId}
group by product_id,company_id,warehouse_id ) warehouseProduct on detail.warehouse_id = warehouseProduct.warehouse_id
and executor.detail_id = #{detailId}
and executor.executor_id = #{executorId}
