    make_id makeId,
    state state,
    cuser cuser,

    cdate cdate,
    uuser uuser,
    udate udate,
    remark remark,
    isdisable isdisable
from vmes_warehouse_in



        where 1=2







                         and id <> #{id} 


                        and id = #{id}




                and code = #{code}


                and company_id = #{companyId}



                and type = #{type}


                and warehouse_id = #{warehouseId}




                and ${queryStr}





    order by ${orderStr}

select id id, company_id companyId code code, type type, warehouse_id warehouseId,
dept_id deptId, dept_name deptName, make_id makeId, state state, cuser cuser,
cdate cdate, uuser uuser, udate udate, remark remark, isdisable isdisable from vmes_warehouse_in
and code LIKE CONCAT(CONCAT(#{code}),'%')
and company_id = #{companyId}
and type = #{type}
and warehouse_id = #{warehouseId}
and ${queryStr}
order by ${orderStr}
