mapper.select:
select id id, company_id companyId, business_id businessId, code code, product_id productId,
warehouse_id warehouseId, count count, before_count beforeCount, after_count afterCount, before_stock_count beforeStockCount,
after_stock_count afterStockCount, operation_count operationCount, business_type businessType, operation operation, cuser cuser,
cdate cdate, remark remark from vmes_warehouse_loginfo
where 1=2
and id <> #{id}
and id = #{id}
and company_id = #{companyId}
and code = #{code}
and product_id = #{productId}
and warehouse_id = #{warehouseId}
and ${queryStr}
order by ${orderStr}

