#{item}

mapper.select:
select

from




        where 1=2







                         and id <> #{id} 


                        and id = #{id}







                and ${queryStr}





    order by ${orderStr}

select
from vmes_warehouse_move_execute execut left join vmes_warehouse_product product on execut.warehouse_product_id = product.id left join vmes_warehouse_product newproduct on execut.new_warehouse_product_id = newproduct.id left join vmes_warehouse warehouse on warehouse.id = product.warehouse_id left join vmes_warehouse newwarehouse on newwarehouse.id = newproduct.warehouse_id left join vmes_user vmesuser on vmesuser.id = execut.executor_id left join vmes_warehouse_move_executor executor on executor.detail_id = execut.detail_id and executor.executor_id = execut.executor_id and executor.isdisable = '1'
and ${queryStr}
and execut.detail_id = #{detailId}
and product.company_id = #{currentCompanyId}
order by execut.isdisable desc ,execut.cdate desc

select execut.id id, product.company_id companyId, product.warehouse_id warehouseId, product.product_id productId, product.code code, round(product.stock_count,2) stockCount, execut.isdisable isdisable, product.qrcode qrcode, round(execut.count,2) actualCount, execut.warehouse_product_id warehouseProductId, execut.new_warehouse_product_id newWarehouseProductId, execut.remark remark, executor.cdate startDate, execut.cdate endDate, vmesuser.user_name executorName, warehouse.path_name name, newwarehouse.path_name newname, prod.name productName from vmes_warehouse_move_execute execut left join vmes_warehouse_product product on execut.warehouse_product_id = product.id left join vmes_warehouse_product newproduct on execut.new_warehouse_product_id = newproduct.id left join vmes_product prod on product.product_id = prod.id
left join vmes_warehouse warehouse on warehouse.id = product.warehouse_id left join vmes_warehouse newwarehouse on newwarehouse.id = newproduct.warehouse_id left join vmes_user vmesuser on vmesuser.id = execut.executor_id left join vmes_warehouse_move_executor executor on executor.detail_id = execut.detail_id and executor.executor_id = execut.executor_id and executor.isdisable = '1'
and ${queryStr}
and execut.detail_id = #{detailId}
and product.company_id = #{currentCompanyId}
order by execut.isdisable desc ,execut.cdate desc
