mapper.select:
select warehouseProduct.code, warehouseProduct.product_id productId,
round(ifnull(product.stock_count, 0), 2) stockCount, product.code productCode, product.name productName, product.name_en productNameEn, product.spec productSpec, product.genre productGenre, genre.name productGenreName,
produnit.unit productUnit, productUnit.name productUnitName
from (
select code, product_id from vmes_warehouse_product
and company_id = #{companyId}
and code = #{code}
group by product_id,code ) warehouseProduct left join vmes_product product on warehouseProduct.product_id = product.id left join vmes_product_unit produnit on warehouseProduct.product_id = produnit.product_id and produnit.type = 1 left join vmes_dictionary genre on product.genre = genre.id left join vmes_dictionary productUnit on produnit.unit = productUnit.id
and (warehouseProduct.code LIKE CONCAT(CONCAT(#{queryText}),'%') or product.code LIKE CONCAT(CONCAT(#{queryText}),'%') or product.name LIKE CONCAT(CONCAT(#{queryText}),'%') )
order by ${orderStr}

select warehouseProduct.product_id productId, round(warehouseProduct.stock_count, 2) stockCount,
product.code productCode, product.name productName, product.name_en productNameEn, product.spec productSpec, product.genre productGenre, genre.name productGenreName, produnit.unit productUnit, productUnit.name productUnitName from ( select product_id,sum(stock_count) stock_count from vmes_warehouse_product
and warehouse_id in ( select id from vmes_warehouse
and company_id = #{companyId}
and dept_id = #{deptId} and dept_place_key = #{deptPlaceKey}
)
and company_id = #{companyId}
group by product_id ) warehouseProduct left join vmes_product product on warehouseProduct.product_id = product.id left join vmes_product_unit produnit on warehouseProduct.product_id = produnit.product_id and produnit.type = 1 left join vmes_dictionary genre on product.genre = genre.id left join vmes_dictionary productUnit on produnit.unit = productUnit.id
