mapper.delete:
        delete from vmes_warehouse_product
        where id in

		#{item}

mapper.select:
select
    id id,
    qrcode qrcode,
    company_id companyId,
    warehouse_id warehouseId,
    product_id productId,

    code code,
    stock_count stockCount,
    cuser cuser,
    cdate cdate,
    uuser uuser,

    audit_date auditDate,
    udate udate,
    remark remark,
    isdisable isdisable,
    version version
from vmes_warehouse_product



        where 1=2







                         and id <> #{id} 


                        and id = #{id}




                and code = #{code}


                and product_id = #{productId}


                and warehouse_id = #{warehouseId}


                and company_id = #{companyId}




                and ${queryStr}





    order by ${orderStr}

select product.id id, product.company_id companyId, product.warehouse_id warehouseId, product.product_id productId, product.code code, round(product.stock_count,2) stockCount, product.isdisable isdisable,
warehouse.path_name name
from vmes_warehouse_product product left join vmes_warehouse warehouse on warehouse.id = product.warehouse_id
and ${queryStr}
and product.product_id = #{productId}
and product.warehouse_id = #{warehouseId}
and product.company_id = #{currentCompanyId}
and product.stock_count > 0
order by product.code asc

select a.id id, a.qrcode qrcode, a.company_id companyId, a.warehouse_id warehouseId, a.product_id productId,
a.code code, a.stock_count stockCount, a.remark remark, a.isdisable isdisable, a.version version,
b.code productCode, b.name productName, b.name_en productNameEn, b.spec productSpec, b.genre productGenre,
b.type productType, b.unit productUnit, b.last_unit productLastUnit, b.last_ratio productLastRatio, b.price productPrice,
b.validity_date productValidityDate, b.isdisable productIsdisable, b.stock_count productStockCount, b.safety_count productSafetyCount
from vmes_warehouse_product a left join vmes_product b on a.product_id = b.id
and a.company_id=#{companyId}
and a.isdisable=#{isdisable}
and b.type=#{productType}
and b.isdisable=#{productIsdisable}

mapper.update:
update vmes_warehouse_product
set
    stock_count = ${stockCount},
    uuser = #{uuser},
    udate = now(),
    version = version + 1


        and id=#{id}



        and code=#{code}


        and product_id=#{productId}


        and warehouse_id=#{warehouseId}



        and version=${version}
